import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './WaterBalancePage.css';

function WaterBalancePage() {
  const [weight, setWeight] = useState('');
  const [activity, setActivity] = useState(0);
  const [waterIntake, setWaterIntake] = useState(null);

  const navigate = useNavigate();

  const calculateWaterIntake = () => {
    if (!weight) {
      alert("Будь ласка, введіть вашу вагу.");
      return;
    }

    fetch('http://localhost:5000/calculate/water-balance', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ weight, activity }),
    })
      .then(response => response.json())
      .then(data => {
        if (data.message) {
          alert(data.message);
        } else {
          setWaterIntake(data.waterIntake);
        }
      })
      .catch(error => {
        console.error('Помилка при розрахунку водного балансу:', error);
        alert('Не вдалося зв\'язатися з сервером.');
      });
  };

  return (
    <div className="calculator-container-with-images">
      <img src="/water_balan.png" alt="Ліва сторона" className="side-image left-image" />

      <div className="calculator-container">
        <h2 className="calculator-title">Калькулятор водного балансу</h2>
        <p className="calculator-info">
          Цей калькулятор допоможе вам визначити добову норму споживання води залежно від вашої ваги та рівня фізичної активності.
        </p>
        <div className="calculator-form">
          <label>Вага (кг):</label>
          <input type="number" value={weight} onChange={(e) => setWeight(e.target.value)} placeholder="Введіть вашу вагу" />

          <label>Фізична активність (хвилин на день):</label>
          <input type="number" value={activity} onChange={(e) => setActivity(e.target.value)} placeholder="Введіть час активності" />

          <button className="calculate-button" onClick={calculateWaterIntake}>Обчислити</button>

          {waterIntake && (
            <div className="result-block">
              <h3>Рекомендована добова норма води:</h3>
              <p>{waterIntake} л</p>
            </div>
          )}

          <button className="back-button" onClick={() => navigate('/')}>На головну</button>
        </div>
      </div>

      <img src="/water_balan.png" alt="Права сторона" className="side-image right-image" />
    </div>
  );
}

export default WaterBalancePage;
