import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CalculatorPage.css';

function CalculatorPage() {
  const [gender, setGender] = useState('female');
  const [age, setAge] = useState('');
  const [height, setHeight] = useState('');
  const [weight, setWeight] = useState('');
  const [activity, setActivity] = useState(1.2);
  const [calorieResult, setCalorieResult] = useState(null);
  const [bmiResult, setBmiResult] = useState(null);

  const navigate = useNavigate(); // Для переходу

  const calculateCalories = () => {
    if (!age || !height || !weight) {
      alert("Будь ласка, заповніть всі поля.");
      return;
    }

    const weightNum = parseFloat(weight);
    const heightNum = parseFloat(height);
    const ageNum = parseFloat(age);

    let bmr;

    if (gender === 'female') {
      bmr = 10 * weightNum + 6.25 * heightNum - 5 * ageNum - 161;
    } else {
      bmr = 10 * weightNum + 6.25 * heightNum - 5 * ageNum + 5;
    }

    const totalCalories = Math.round(bmr * activity);
    setCalorieResult(totalCalories);
  };

  const calculateBMI = () => {
    if (!height || !weight) {
      alert("Введіть зріст та вагу для розрахунку ІМТ.");
      return;
    }

    const heightMeters = parseFloat(height) / 100;
    const bmi = (parseFloat(weight) / (heightMeters * heightMeters)).toFixed(1);

    let category = '';
    if (bmi < 16) category = 'Виражений дефіцит маси тіла';
    else if (bmi < 18.5) category = 'Недостатня (дефіцит) маса тіла';
    else if (bmi < 25) category = 'Нормальна маса тіла';
    else if (bmi < 30) category = 'Надлишкова маса тіла';
    else if (bmi < 35) category = 'Ожиріння першого ступеня';
    else if (bmi < 40) category = 'Ожиріння другого ступеня';
    else category = 'Ожиріння третього ступеня';

    setBmiResult({ bmi, category });
  };

  return (
    <div className="calculator-container">
      <h2>Калькулятор калорій та ІМТ</h2>

      <p className="calculator-info">
        Використовуйте цей калькулятор для розрахунку вашої добової норми калорій та індексу маси тіла (ІМТ).
      </p>

      <div className="calculator-form">

        <label>Стать:</label>
        <select value={gender} onChange={(e) => setGender(e.target.value)}>
          <option value="female">Жіноча</option>
          <option value="male">Чоловіча</option>
        </select>

        <label>Вік (років):</label>
        <input type="number" value={age} onChange={(e) => setAge(e.target.value)} placeholder="Введіть ваш вік" />

        <label>Зріст (см):</label>
        <input type="number" value={height} onChange={(e) => setHeight(e.target.value)} placeholder="Введіть ваш зріст" />

        <label>Вага (кг):</label>
        <input type="number" value={weight} onChange={(e) => setWeight(e.target.value)} placeholder="Введіть вашу вагу" />

        <label>Рівень активності:</label>
        <select value={activity} onChange={(e) => setActivity(parseFloat(e.target.value))}>
          <option value="1.2">Сидячий спосіб життя (1.2)</option>
          <option value="1.375">Легка активність (1.375)</option>
          <option value="1.55">Помірна активність (1.55)</option>
          <option value="1.725">Висока активність (1.725)</option>
          <option value="1.9">Дуже висока активність (1.9)</option>
        </select>

        <button className="calculate-button" onClick={calculateCalories}>Обчислити калорії</button>
        <button className="calculate-button" onClick={calculateBMI}>Розрахувати ІМТ</button>

        {calorieResult && (
          <div className="result-block">
            <h3>Ваша добова норма калорій:</h3>
            <p>{calorieResult} ккал</p>
          </div>
        )}

        {bmiResult && (
          <div className="result-block">
            <h3>Ваш ІМТ: {bmiResult.bmi}</h3>
            <p>Категорія: <strong>{bmiResult.category}</strong></p>
          </div>
        )}

        <button
          className="back-button"
          onClick={() => navigate('/')}
        >
          На головну
        </button>
      </div>
    </div>
  );
}

export default CalculatorPage;
