import React, { useState } from 'react';
import './Header.css';
import { Menu, X } from 'lucide-react'; // Иконки для бургер-меню (можно заменить на любые другие)

function Header() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [language, setLanguage] = useState('UA');

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
    // здесь можно добавить логику переключения языка сайта
  };

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo-title">
          <img src="/logo.png" alt="Здорове життя" className="logo" />
          <h1 className="site-title">Здорове життя</h1>
        </div>

        <div className="header-controls">
          <select value={language} onChange={handleLanguageChange} className="language-selector">
            <option value="UA">UA</option>
            <option value="EN">EN</option>
            <option value="RU">RU</option>
          </select>

          <nav className="nav-buttons">
            <button className="nav-button">Вхід</button>
            <button className="nav-button">Реєстрація</button>
            <button className="nav-button">Вихід</button>
          </nav>

          <button className="burger-button" onClick={() => setMenuOpen(!menuOpen)}>
            {menuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </div>

      {menuOpen && (
        <div className="burger-menu">
          <a href="#calories" className="burger-link">Калькулятор калорій</a>
          <a href="#water" className="burger-link">Водний баланс</a>
          <a href="#products" className="burger-link">Калорійність продуктів</a>
          <a href="#diets" className="burger-link">Дієти</a>
          <a href="#activity" className="burger-link">Активність</a>
        </div>
      )}
    </header>
  );
}

export default Header;
