import React, { useState } from 'react';
import './Header.css';

function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo-title">
          <img src="/logo.png" alt="Здорове життя" className="logo" />
          <h1 className="site-title">Здорове життя</h1>
        </div>

        <nav className="nav-buttons">
          <button className="nav-button">Вхід</button>
          <button className="nav-button">Реєстрація</button>
          <button className="nav-button">Вихід</button>

          {/* Бургер-кнопка */}
          <div className="burger-menu">
            <button className="burger-button" onClick={toggleMenu}>
              ☰
            </button>
            {isMenuOpen && (
              <div className="burger-dropdown">
                <a href="#calculator">Калькулятор калорій</a>
                <a href="#water">Водний баланс</a>
                <a href="#products">Калорійність продуктів</a>
                <a href="#diets">Дієти</a>
                <a href="#activity">Активність</a>
              </div>
            )}
          </div>
        </nav>
      </div>
    </header>
  );
}

export default Header;
