import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import PageLayout from '../components/PageLayout';
import './WaterBalancePage.css';

function WaterBalancePage() {
  const [weight, setWeight] = useState('');
  const [activity, setActivity] = useState(1.2);
  const [waterNeed, setWaterNeed] = useState(null);
  const [drunkWater, setDrunkWater] = useState(0);
  const [waterMessage, setWaterMessage] = useState('');

  const navigate = useNavigate();

  const calculateWaterNeed = () => {
    if (!weight || weight <= 0) {
      alert("Будь ласка, введіть коректну вагу.");
      return;
    }

    fetch('http://localhost:5000/calculate/water-need', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ weight, activity }),
    })
    .then(response => response.json())
    .then(data => {
      if (data.message) {
        alert(data.message);
      } else {
        setWaterNeed(data.waterNeed);
        setWaterMessage(''); // Reset message on new calculation
      }
    })
    .catch(error => {
      console.error('Помилка при розрахунку норми води:', error);
      alert('Не вдалося зв\'язатися з сервером для розрахунку.');
    });
  };

  const addWater = (amount) => {
    fetch('http://localhost:5000/calculate/update-water', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        drunkWater: drunkWater,
        amount: amount,
        waterNeed: waterNeed
      }),
    })
    .then(response => response.json())
    .then(data => {
      setDrunkWater(data.newDrunkWater);
      setWaterMessage(data.waterMessage);
    })
    .catch(error => {
      console.error('Помилка оновлення води:', error);
      alert('Не вдалося оновити дані про воду.');
    });
  };

  const pageDescription = "Щоденне споживання води необхідне для підтримки нормальної роботи всіх систем організму. Орієнтовно потрібно 30 мл води на 1 кг ваги тіла на добу. Використовуйте цей лічильник споживання води для щоденного дослідження випитої вами води.";

  return (
    <PageLayout 
      pageTitle="Водний баланс"
      pageDescription={pageDescription}
      mainClassName="wb-container"
    >
      <div className="wb-form-section">
        <h3>Розрахунок добової норми води</h3>

        <label>Ваша вага (кг):</label>
        <input
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
          placeholder="Введіть вагу"
        />

        <label>Рівень активності:</label>
        <select value={activity} onChange={(e) => setActivity(parseFloat(e.target.value))}>
          <option value="1.2">Сидячий спосіб життя — 1.2</option>
          <option value="1.375">Легка активність — 1.375</option>
          <option value="1.55">Помірна активність — 1.55</option>
          <option value="1.725">Висока активність — 1.725</option>
          <option value="1.9">Дуже висока активність — 1.9</option>
        </select>

        <button className="wb-calculate-button" onClick={calculateWaterNeed}>Обчислити норму води</button>

        {waterNeed && (
          <div className="wb-result-block">
            <h3>Ваша добова норма води: {waterNeed} л</h3>
          </div>
        )}
      </div>

      <div className="wb-form-section">
        <h3>Облік випитої води</h3>
        <p className="wb-drunk-water">Випито на даний момент: {drunkWater.toFixed(2)} л</p>
        <div className="wb-button-group">
          <button className="wb-calculate-button" onClick={() => addWater(0.25)}>+ 0.25 л</button>
          <button className="wb-calculate-button" onClick={() => addWater(0.5)}>+ 0.5 л</button>
          <button className="wb-calculate-button" onClick={() => addWater(1)}>+ 1 л</button>
        </div>

        {waterMessage && (
          <div className="wb-result-block">
            <p>{waterMessage}</p>
          </div>
        )}
      </div>

      <button
        className="wb-back-button"
        onClick={() => navigate('/')}
      >
        На головну
      </button>
    </PageLayout>
  );
}

export default WaterBalancePage;
