import React, { useState } from 'react';
import './WaterBalancePage.css';

function WaterBalancePage() {
  const [weight, setWeight] = useState('');
  const [activity, setActivity] = useState(1);
  const [waterNeed, setWaterNeed] = useState(null);
  const [drunkWater, setDrunkWater] = useState('');
  const [waterMessage, setWaterMessage] = useState('');

  const calculateWaterNeed = () => {
    if (!weight) {
      alert("Будь ласка, введіть вашу вагу.");
      return;
    }
    const base = weight * 0.03; // 30 мл на кг → в літрах
    const total = (base * activity).toFixed(2);
    setWaterNeed(total);
    setWaterMessage('');
  };

  const checkWaterIntake = () => {
    if (!waterNeed || !drunkWater) {
      alert("Спочатку обчисліть норму води та введіть скільки вже випили.");
      return;
    }
    const remaining = (waterNeed - parseFloat(drunkWater)).toFixed(2);
    if (remaining <= 0) {
      setWaterMessage(`Відмінно! Ви вже випили свою норму води на сьогодні.`);
    } else {
      setWaterMessage(`Потрібно випити ще ${remaining} л води.`);
    }
  };

  return (
    <div className="water-container">
      <h2>Водний баланс</h2>
      <p className="info">
        Вода — основа життя. Щоб підтримувати здоров'я, необхідно пити достатньо води щодня.
        Орієнтовно потрібно <strong>30 мл на 1 кг ваги</strong>, але активність теж впливає.
      </p>

      <div className="form-section">
        <label>Ваша вага (кг):</label>
        <input
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
          placeholder="Введіть вагу"
        />

        <label>Рівень активності:</label>
        <select value={activity} onChange={(e) => setActivity(parseFloat(e.target.value))}>
          <option value="1">Малорухомий спосіб (×1)</option>
          <option value="1.2">Легка активність (×1.2)</option>
          <option value="1.4">Середня активність (×1.4)</option>
          <option value="1.6">Висока активність (×1.6)</option>
        </select>

        <button className="calculate-button" onClick={calculateWaterNeed}>Обчислити норму води</button>

        {waterNeed && (
          <div className="result-block">
            <h3>Ваша норма води: {waterNeed} л / день</h3>
          </div>
        )}
      </div>

      {waterNeed && (
        <div className="form-section">
          <label>Скільки води вже випито (л):</label>
          <input
            type="number"
            value={drunkWater}
            onChange={(e) => setDrunkWater(e.target.value)}
            placeholder="Введіть кількість"
          />
          <button className="calculate-button" onClick={checkWaterIntake}>Перевірити результат</button>
        </div>
      )}

      {waterMessage && (
        <div className="result-block">
          <p>{waterMessage}</p>
        </div>
      )}
    </div>
  );
}

export default WaterBalancePage;
