import React, { useState, useEffect } from 'react';
import './ProductsPage.css';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [totals, setTotals] = useState({ calories: 0, protein: 0, fat: 0, carbs: 0 });
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10); // Кількість продуктів на сторінці
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/protucts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Помилка при завантаженні продуктів:', error));
  }, []);

  // Скидаємо сторінку на першу при пошуку
  useEffect(() => {
    setCurrentPage(1);
  }, [searchQuery]);

  const handleProductSelect = (product, isSelected) => {
    let newSelected;
    if (isSelected) {
      newSelected = [...selectedProducts, product];
    } else {
      newSelected = selectedProducts.filter(p => p._id !== product._id);
    }
    setSelectedProducts(newSelected);
  };

  useEffect(() => {
    if (selectedProducts.length === 0) {
      setTotals({ calories: 0, protein: 0, fat: 0, carbs: 0 });
      return;
    }

    const productIds = selectedProducts.map(p => p._id);

    fetch('http://localhost:5000/protucts/calculate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ productIds }),
    })
      .then(response => response.json())
      .then(data => setTotals(data))
      .catch(error => console.error('Помилка при обчисленні на сервері:', error));

  }, [selectedProducts]);
  
  // Логіка пошуку та пагінації
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().startsWith(searchQuery.toLowerCase())
  );
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentProducts = filteredProducts.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);

  const nextPage = () => {
    setCurrentPage(prev => (prev < totalPages ? prev + 1 : prev));
  };

  const prevPage = () => {
    setCurrentPage(prev => (prev > 1 ? prev - 1 : prev));
  };

  return (
    <div className="products-page">
      <h1>Калькулятор калорій</h1>

      <div className="product-list">
        <h2>Список продуктів</h2>

        <div className="search-container">
            <input
            type="text"
            placeholder="Пошук продукту за назвою..."
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
            />
        </div>

        {currentProducts.map(product => (
          <div key={product._id} className="product-item">
            <label>
              <input
                type="checkbox"
                checked={selectedProducts.some(p => p._id === product._id)}
                onChange={e => handleProductSelect(product, e.target.checked)}
              />
              {`${product.name} (К: ${product.calories}, Б: ${product.protein}, Ж: ${product.fat}, В: ${product.carbs})`}
            </label>
          </div>
        ))}
        {totalPages > 1 && (
            <div className="pagination">
                <button onClick={prevPage} disabled={currentPage === 1}>
                    Назад
                </button>
                <span>{currentPage} / {totalPages}</span>
                <button onClick={nextPage} disabled={currentPage === totalPages}>
                    Далі
                </button>
            </div>
        )}
      </div>

      <div className="totals">
        <h2>Ваш результат</h2>
        <table>
          <thead>
            <tr>
              <th>Показник</th>
              <th>Кількість</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Калорії</td>
              <td>{totals.calories.toFixed(2)}</td>
            </tr>
            <tr>
              <td>Білки, г</td>
              <td>{totals.protein.toFixed(2)}</td>
            </tr>
            <tr>
              <td>Жири, г</td>
              <td>{totals.fat.toFixed(2)}</td>
            </tr>
            <tr>
              <td>Вуглеводи, г</td>
              <td>{totals.carbs.toFixed(2)}</td>
            </tr>
          </tbody>
        </table>

        {selectedProducts.length > 0 && (
            <div className="selected-products-list">
                <h3>Вибрані продукти:</h3>
                <ul>
                    {selectedProducts.map(product => (
                        <li key={`selected-${product._id}`}>{product.name}</li>
                    ))}
                </ul>
            </div>
        )}
      </div>
    </div>
  );
}

export default ProductsPage;
