import React, { useState, useEffect } from 'react';
import './ProductsPage.css';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [totals, setTotals] = useState({ calories: 0, protein: 0, fat: 0, carbs: 0 });

  useEffect(() => {
    fetch('http://localhost:5000/protucts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Ошибка при загрузке продуктов:', error));
  }, []);

  const handleProductSelect = (product, isSelected) => {
    let newSelected;
    if (isSelected) {
      newSelected = [...selectedProducts, product];
    } else {
      newSelected = selectedProducts.filter(p => p._id !== product._id);
    }
    setSelectedProducts(newSelected);
  };

  useEffect(() => {
    const newTotals = selectedProducts.reduce(
      (acc, product) => {
        acc.calories += product.calories;
        acc.protein += product.protein;
        acc.fat += product.fat;
        acc.carbs += product.carbs;
        return acc;
      },
      { calories: 0, protein: 0, fat: 0, carbs: 0 }
    );
    setTotals(newTotals);
  }, [selectedProducts]);

  return (
    <div className="products-page">
      <h1>Калькулятор калорий</h1>

      <div className="product-list">
        <h2>Список продуктов</h2>
        {products.map(product => (
          <div key={product._id} className="product-item">
            <label>
              <input
                type="checkbox"
                onChange={e => handleProductSelect(product, e.target.checked)}
              />
              {product.name} (100г)
            </label>
          </div>
        ))}
      </div>

      <div className="totals">
        <h2>Ваш результат</h2>
        <table>
          <thead>
            <tr>
              <th>Показатель</th>
              <th>Количество</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Калории</td>
              <td>{totals.calories.toFixed(2)}</td>
            </tr>
            <tr>
              <td>Белки, г</td>
              <td>{totals.protein.toFixed(2)}</td>
            </tr>
            <tr>
              <td>Жиры, г</td>
              <td>{totals.fat.toFixed(2)}</td>
            </tr>
            <tr>
              <td>Углеводы, г</td>
              <td>{totals.carbs.toFixed(2)}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ProductsPage;
