import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './WaterBalancePage.css';

function WaterBalancePage() {
  const [weight, setWeight] = useState('');
  const [activity, setActivity] = useState(1);
  const [waterNeed, setWaterNeed] = useState(null);
  const [drunkWater, setDrunkWater] = useState(0);
  const [waterMessage, setWaterMessage] = useState('');

  const navigate = useNavigate();

  const calculateWaterNeed = () => {
    if (!weight || weight <= 0) {
      alert("Будь ласка, введіть коректну вагу.");
      return;
    }
    const baseNeed = weight * 0.03;
    const totalNeed = (baseNeed * activity).toFixed(2);
    setWaterNeed(totalNeed);
    setDrunkWater(0);
    setWaterMessage("");
  };

  const addWater = (amount) => {
    const newTotal = parseFloat(drunkWater) + amount;
    setDrunkWater(newTotal);
    if (newTotal >= waterNeed) {
      setWaterMessage("Відмінно! Ви вже випили необхідну кількість води на сьогодні.");
    } else {
      const remaining = (waterNeed - newTotal).toFixed(2);
      setWaterMessage(`Потрібно випити ще ${remaining} л води.`);
    }
  };

  return (
    <div className="water-container">
      <h2>Водний баланс</h2>
      <p className="info">
        Щоденне споживання води життєво необхідне для нормального функціонування організму.
        Орієнтовно потрібно 30 мл води на 1 кг ваги на добу. Коригуйте норму залежно від фізичної активності.
      </p>

      <div className="form-section">
        <label>Ваша вага (кг):</label>
        <input
          type="number"
          value={weight}
          onChange={(e) => setWeight(e.target.value)}
          placeholder="Введіть вагу"
        />

        <label>Рівень активності:</label>
        <select value={activity} onChange={(e) => setActivity(parseFloat(e.target.value))}>
          <option value="1">Малорухомий спосіб (×1)</option>
          <option value="1.2">Легка активність (×1.2)</option>
          <option value="1.4">Середня активність (×1.4)</option>
          <option value="1.6">Висока активність (×1.6)</option>
        </select>

        <button className="calculate-button" onClick={calculateWaterNeed}>Обчислити норму води</button>
      </div>

      {waterNeed && (
        <div className="result-block">
          <h3>Ваша добова норма води: {waterNeed} л</h3>
          <h4>Випито: {drunkWater.toFixed(2)} л</h4>
          <div className="button-group">
            <button className="calculate-button" onClick={() => addWater(0.25)}>+ 0.25 л</button>
            <button className="calculate-button" onClick={() => addWater(0.5)}>+ 0.5 л</button>
            <button className="calculate-button" onClick={() => addWater(1)}>+ 1 л</button>
          </div>
          {waterMessage && (
            <div className="result-block">
              <p>{waterMessage}</p>
            </div>
          )}
        </div>
      )}

      <button className="back-button" onClick={() => navigate('/')}>На головну</button>
    </div>
  );
}

export default WaterBalancePage;
